  if (sparsity->dim_metadata()->size() != total_dims) {
    return absl::nullopt;
  }

  const int block_rank = total_dims - tensor.shape()->size();
  if (block_rank > 0 && (sparsity->block_map() == nullptr ||
                         sparsity->block_map()->size() != block_rank)) {
    return absl::nullopt;
  }

