Status HloInstruction::GetBackendConfigInternal(
    tensorflow::protobuf::Message* proto) const {
  proto->Clear();

  if (auto* proto_ptr = backend_config_.GetProtoPtr()) {
    proto->CopyFrom(*proto_ptr);
    return Status::OK();
  }

  auto& raw_string = raw_backend_config_string();
  // Empty string does not parse as valid JSON, but it's a valid backend config,
  // corresponding to the empty proto.
  if (raw_string.empty()) {
    return Status::OK();
  }
  TF_RETURN_IF_ERROR(tensorflow::HumanReadableJsonToProto(raw_string, proto));
  backend_config_.SetProto(*proto);
  return Status::OK();
}

// client-owned threadpool.
void OnReady(absl::AnyInvocable<void(T) &&> callback) {
  CHECK(IsValid());
  promise_ref_.AndThen([promise = promise_ref_.AsPtr(),
                        callback = std::move(callback)]() mutable {
    DCHECK(promise.IsConcrete());
    if constexpr (std::is_copy_constructible_v<T>) {
      std::move(callback)(*promise);
      return;
    }
    DCHECK_EQ(promise.value()->NumRef(), 1);
    std::move(callback)(std::move(*promise));
  });
}

// Indicates that event will not complete until after this becomes ready.
//
// May safely be called with event==nullptr in which case AssertHappensBefore
// has no effect.
void AssertHappensBefore(ScopedAsyncTrackingEvent* event) {
  CHECK(IsValid());
  if (event) {
    event->AddDependency(promise_ref_.CopyRCRef());

  return NanoToPico(line_->timestamp_ns()) + event_->offset_ps();
}

